import { useState } from 'react';
import axios from 'axios';
import { toast } from 'sonner';
import { Button } from '../components/ui/button';
import { Label } from '../components/ui/label';
import { useAuth } from '../contexts/AuthContext';

const SettingsPanel = ({ breakAfter, apiUrl, onSettingsChange }) => {
  const { token } = useAuth();
  const handleUpdateBreak = async value => {
    try {
      await axios.put(
        `${apiUrl}/settings/break-period?break_after=${value}`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      toast.success(`Break period updated to after Period ${value}`);
      onSettingsChange(value);
    } catch {
      toast.error('Failed to update break period');
    }
  };

  const handleLoadDemoSchedules = async () => {
    if (
      !window.confirm(
        'This will replace all existing schedules with demo data. Continue?'
      )
    )
      return;

    try {
      await axios.post(
        `${apiUrl}/demo/load-schedules`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      toast.success('Demo schedules loaded successfully!');
      onSettingsChange();
    } catch {
      toast.error('Failed to load demo schedules');
    }
  };

  const handleClearAllSchedules = async () => {
    if (
      !window.confirm(
        'This will delete ALL schedules and changelogs. Are you sure?'
      )
    )
      return;

    try {
      await axios.post(
        `${apiUrl}/demo/clear-schedules`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      toast.success('All schedules cleared');
      onSettingsChange();
    } catch {
      toast.error('Failed to clear schedules');
    }
  };

  return (
    <div className="space-y-4">
      <div className="glass p-6 rounded-2xl">
        <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-6">
          System Settings
        </h2>

        <div className="space-y-4">
          {/* Break Period Setting */}
          <div className="flex items-center justify-between p-4 bg-white dark:bg-slate-800 rounded-xl border border-gray-200 dark:border-slate-700">
            <div>
              <Label className="text-base font-medium text-gray-900 dark:text-white">
                Break Period Position
              </Label>
              <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">
                Currently: After Period {breakAfter}
              </p>
            </div>
            <div className="flex gap-2">
              <Button
                onClick={() => handleUpdateBreak(3)}
                variant={breakAfter === 3 ? 'default' : 'outline'}
                data-testid="break-after-3"
              >
                After Period 3
              </Button>
              <Button
                onClick={() => handleUpdateBreak(4)}
                variant={breakAfter === 4 ? 'default' : 'outline'}
                data-testid="break-after-4"
              >
                After Period 4
              </Button>
            </div>
          </div>

          {/* Demo Data Management */}
          <div className="p-4 bg-white dark:bg-slate-800 rounded-xl border border-gray-200 dark:border-slate-700">
            <Label className="text-base font-medium text-gray-900 dark:text-white">
              Demo Timetable Management
            </Label>
            <p className="text-sm text-gray-500 dark:text-gray-400 mt-1 mb-4">
              Load pre-configured demo schedules for all teachers or clear all
              existing data
            </p>
            <div className="flex gap-3">
              <Button
                onClick={handleLoadDemoSchedules}
                className="bg-blue-600 hover:bg-blue-700 text-white"
                data-testid="load-demo-schedules-button"
              >
                <svg
                  className="w-4 h-4 mr-2"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-8l-4-4m0 0L8 8m4-4v12"
                  />
                </svg>
                Load Demo Schedules
              </Button>
              <Button
                onClick={handleClearAllSchedules}
                variant="outline"
                className="border-red-500 text-red-600 hover:bg-red-50 dark:hover:bg-red-900/20"
                data-testid="clear-all-schedules-button"
              >
                <svg
                  className="w-4 h-4 mr-2"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                  />
                </svg>
                Clear All Schedules
              </Button>
            </div>
            <div className="mt-3 p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
              <p className="text-xs text-blue-900 dark:text-blue-300">
                <strong>Note:</strong> Demo schedules include realistic
                timetables for all 4 teachers (Sagnik, Nadeem, Prinshu,
                Abhishek) with subjects like Mathematics, Physics, English, and
                History across all weekdays.
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SettingsPanel;
