import { useState } from 'react';
import axios from 'axios';
import { toast } from 'sonner';
import { Plus, Trash2 } from 'lucide-react';
import { Button } from '../components/ui/button';
import { Input } from '../components/ui/input';
import { Label } from '../components/ui/label';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '../components/ui/dialog';
import { useAuth } from '../contexts/AuthContext';

const TeacherManagement = ({ teachers, onTeacherChange, apiUrl }) => {
  const { token } = useAuth();
  const [showAddTeacher, setShowAddTeacher] = useState(false);
  const [newTeacher, setNewTeacher] = useState({
    username: '',
    password: '',
    name: '',
  });

  const handleAddTeacher = async () => {
    if (!newTeacher.username || !newTeacher.password || !newTeacher.name) {
      toast.error('Please fill all fields');
      return;
    }

    try {
      await axios.post(`${apiUrl}/users`, newTeacher, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success('Teacher added successfully');
      setShowAddTeacher(false);
      setNewTeacher({ username: '', password: '', name: '' });
      onTeacherChange();
    } catch {
      toast.error('Failed to add teacher');
    }
  };

  const handleDeleteTeacher = async teacherId => {
    if (!window.confirm('Are you sure you want to delete this teacher?')) {
      return;
    }

    try {
      await axios.delete(`${apiUrl}/users/${teacherId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success('Teacher deleted successfully');
      onTeacherChange();
    } catch {
      toast.error('Failed to delete teacher');
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">Teacher Management</h2>
        <Button onClick={() => setShowAddTeacher(true)}>
          <Plus className="w-4 h-4 mr-2" />
          Add Teacher
        </Button>
      </div>

      <div className="grid gap-4">
        {teachers.map(teacher => (
          <div
            key={teacher.id}
            className="flex items-center justify-between p-4 border rounded-lg"
          >
            <div>
              <h3 className="font-medium">{teacher.name}</h3>
              <p className="text-sm text-muted-foreground">
                @{teacher.username}
              </p>
            </div>
            <Button
              variant="destructive"
              size="sm"
              onClick={() => handleDeleteTeacher(teacher.id)}
            >
              <Trash2 className="w-4 h-4" />
            </Button>
          </div>
        ))}
      </div>

      <Dialog open={showAddTeacher} onOpenChange={setShowAddTeacher}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Add New Teacher</DialogTitle>
            <DialogDescription>
              Create a new teacher account with login credentials.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="name">Full Name</Label>
              <Input
                id="name"
                value={newTeacher.name}
                onChange={e =>
                  setNewTeacher({ ...newTeacher, name: e.target.value })
                }
                placeholder="Enter full name"
              />
            </div>
            <div>
              <Label htmlFor="username">Username</Label>
              <Input
                id="username"
                value={newTeacher.username}
                onChange={e =>
                  setNewTeacher({ ...newTeacher, username: e.target.value })
                }
                placeholder="Enter username"
              />
            </div>
            <div>
              <Label htmlFor="password">Password</Label>
              <Input
                id="password"
                type="password"
                value={newTeacher.password}
                onChange={e =>
                  setNewTeacher({ ...newTeacher, password: e.target.value })
                }
                placeholder="Enter password"
              />
            </div>
            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                onClick={() => setShowAddTeacher(false)}
              >
                Cancel
              </Button>
              <Button onClick={handleAddTeacher}>Add Teacher</Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default TeacherManagement;
